INSERT INTO "public"."recipe" VALUES (21, 1, 4, '2023-9-19', 'Рулетики с изюмом', 'ruletiki_s_izyumom', '/recipe/ruletiki_s_izyumom/photo.jpg', '/recipe/ruletiki_s_izyumom/preview.jpg', '', 'Яйца, молоко, ванилин, просеянную муку и подсластитель смешать до однородности (если необходимо, то добавить воду) и испечь блины. Творог смешать с изюмом и подсластителем по вкусу. Выложить полученную начинку на каждый блинчик и свернуть в рулетики. По желанию можно разрезать рулетики наискосок.');
